INSERT INTO `school` VALUES ('3566', '广州幼儿师范高等专科学校', '广东省', '0', '0', '44', '0', '0', '', '专科（高职）', '', '专科（高职）', '公办', '', '{}', '广东', '广州市', '天河区', 'gzyzzsb@163.com', '科教城校区：广东省广州市增城区朱村街秀山村,永福校区：广东省广州市越秀区先烈东横路60-1号', '510610', '', '020-38806606,020-38807605,020-38984470', '', '广州市幼儿师范学校直属广州市教育局，是一所培养幼儿师资和艺术类人才的全日制公立省级重点中等职业学校，同时也是省、市学前教育职后培训基地。学校现有2个校区（林和校区、永福校区），60个教学班（包括：55个普通教学班、4个高职班、1个华文班），全日制在校生3006人，设有儿童心理实验室、奥尔夫音乐教室、早期教育实训室、钢琴练习室、陶艺室、舞蹈室、电脑', '0', '0', '0', '0', 0, '0', '0', '0', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2');
