INSERT INTO `school` VALUES ('3423', '郑州西亚斯学院', '河南省教育厅', '0', '0', '41', '1998', '2400', '', '普通本科', '综合类', '普通本科', '民办', '', '{}', '河南', '郑州市', '新郑市', 'ao@sias.edu.cn', '河南省郑州新郑市人民路东段168号', '451150', 'http://ao.sias.edu.cn/', '0371-62606666', '', '郑州西亚斯学院前身为郑州大学西亚斯国际学院，始建于1998年。郑州大学西亚斯国际学院是由美国西亚斯集团公司投资，与郑州大学、美国堪萨斯州富特海斯州立大学（FortHaysStateUniversity，英文缩写FHSU)合作的中外合作办学机构，是河南省首家被国务院学位委员会批准可以实施境外学士学位教育合作项目的全日制本科院校，也是首批被中国教育部中外合作办学评估合格的高校。', '0', '0', '0', '0', 0, '0', '0', '0', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', '2');
