INSERT INTO `school` VALUES ('3348', '江苏旅游职业学院', '江苏省', '0', '0', '32', '1959', '1175', '', '专科（高职）', '其他', '专科（高职）', '公办', '', '{}', '江苏', '扬州市', '', '113617790@qq.com', '江苏省扬州市毓秀路88号', '225000', 'http://www.jstc.edu.cn/zs/', '0514-87431880,0514-87431888', '', '江苏旅游职业学院是一所省属公办全日制专科高校。学院坐落于中国历史文化名城——扬州。学院有三个校区，其中九龙湖校区占地845亩、扬子津校区占地320亩、瘦西湖校区占地11亩。九龙湖校区地处扬州临港新城中心地带，北望高旻寺、南眺扬子江、西依古运河、东临凤栖湖，按5A景区标准建设，集萃扬州园林精华，校园即公园、建筑即景点、情境即学境，是理想的求学圣地', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
