INSERT INTO `school` VALUES ('3337', '吉林职业技术学院', '吉林省教育厅', '0', '0', '22', '2015', '922', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '吉林', '延边朝鲜族自治州', '', '', '吉林省延边朝鲜族自治州龙井市学府路739', '133400', 'http://www.jlhtedu.com/zsjy/', '0433-5033999', '', '吉林职业技术学院是经吉林省人民政府批准、教育部备案的全日制普通高等院校，地处有“全国民族团结进步模范自治州”、“中国十佳冰雪旅游城市”美誉的延边朝鲜族自治州，西邻国家级4A级景区琵岩山风景区，南望两岸稻花香的海兰江。学校立足于服务地方经济建设，着眼于学生终身可持续发展，校区内松杉叠翠、环境优雅，教学、生活、服务功能区兼备，拥有高素质的', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
