INSERT INTO `school` VALUES ('3281', '深圳技术大学', '广东省', '0', '0', '44', '2016', '2325', '深圳技术大学（筹）', '普通本科', '理工类', '普通本科', '公办', '', '{}', '广东', '深圳市', '坪山区', 'zsb@sztu.edu.cn', '广东省深圳市坪山区兰田路3002号', '518118', 'http://zs.sztu.edu.cn/', '0755-23256666', '', '办学定位深圳技术大学是广东省和深圳市高起点、高水平、高标准建设的本科层次公办普通高等学校。2015年，深圳市委市政府开始筹建深圳技术大学。2016年3月，深圳市人民政府办公厅发布关于设立深圳技术大学筹备办公室的通知。2017年7月，深圳市机构编制委员会发布关于设立深圳技术大学（筹）的通知。2017年9月、2018年9月深圳技术大学（筹）依托深圳大学分别招收了226人', '0', '0', '0', '1', 0, '346', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('3280', '云南财经职业学院', '云南省', '0', '0', '53', '1934', '253', '', '专科（高职）', '财经类', '专科（高职）', '公办', '', '{}', '云南', '昆明市', '盘龙区', 'czyzsjyc@163.com', '云南省昆明市五华区龙泉路408号', '650222', 'http://zsw.ynczy.edu.cn/', '0871-65810290,0871-65841605', '', '办学历程云南财经职业学院是经云南省人民政府批准、教育部备案的全日制公办普通高等财经职业院校，其前身为始建于1934年11月6日的云南省立鼎新初级商业职业学校，1942年更名为“云南省立昆华高级商业职业学校”。在此基础上，1959年组建云南省财经学校。2015年2月，经云南省人民政府批准、教育部备案，学校升级为专科层次的全日制公办普通高等财经职业院校，并命名为', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('3279', '海南健康管理职业技术学院', '海南省教育厅', '0', '0', '46', '2016', '800', '', '专科（高职）', '医药类', '专科（高职）', '民办', '', '{}', '海南', '海口市', '秀英区', 'hnjkglzyjsxy@163.com', '海南省澄迈县文化北路1号', '', 'http://zsw.hainhmc.edu.cn/', '0898-32115011,32115022,32115033', '', '海南健康管理职业技术学院（国标代码：14639），是海南省人民政府批准设置，教育部备案的全国第一家独立设置、社会力量举办，面向全国高考招生的健康管理类高职院校。学院位于省会海口市桂林洋大学城。这里高校聚集、文化氛围浓郁，是海口高新文化的“摇篮”。已具备全方位办学条件的学院分校位于有“世界长寿之乡”之称的澄迈。学院以海南创建国际教育创新岛为', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
