INSERT INTO `school` VALUES ('3275', '三亚中瑞酒店管理职业学院', '海南省教育厅', '0', '0', '46', '2017', '362', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '海南', '三亚市', '海棠区', 'info@his.edu.cn', '海南省三亚市海棠区湾坡路108号', '572014', 'https://www.his.edu.cn/index/recruit/index', '0898-88695566', '', '三亚中瑞酒店管理职业学院是一所由海南省人民政府批准设立的全日制普通专科院校。学院位于享有“国家海岸”美誉的三亚市海棠湾，毗邻亚洲最大的单体免税城和海棠湾国际酒店带。学院紧紧围绕海南自贸港和“三区一中心”建设所急需的泛服务业人才需求，开设酒店管理、旅游管理、经济与管理三个教学系，培养符合海南自贸港建设发展亟需的泛服务业国际高端人才。', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
