INSERT INTO `school` VALUES ('3239', '广东茂名幼儿师范专科学校', '广东省', '0', '0', '44', '2016', '1200', '广东石油化工学院高州师范学院', '专科（高职）', '师范类', '专科（高职）', '公办', '', '{}', '广东', '茂名市', '茂南区', 'zjk3383069@163.com', '茂名校区：广东省茂名市西城西路6号,高州校区：广东省高州市高师路1号', '525000', 'https://zs.gdpnc.edu.cn/', '0668-3383561,0668-6618889', '', '广东茂名幼儿师范专科学校位于祖国南海之滨、“好心之城”、中国魅力城市——茂名。茂名是国家“一带一路”战略中“一带”的重要城市，地处粤港澳大湾区、北部湾经济区、海南自由贸易试验区三大经济圈的交汇处，地理位置优越，交通方便，风景优美、气候宜人。学校是广东省人民政府批准、教育部备案的全日制公办普通高等专科院校，是广东省第一所幼教师范高等', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
