INSERT INTO `school` VALUES ('3158', '山东职业学院西校区', '山东省教育厅', '0', '0', '37', '2000', '160', '齐鲁师范学院高新校区', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '山东', '济南市', '济南高新技术产业开发区', 'zb2036@163.com', '山东省济南市高新技术开发区新泺大街201号', '250101', '', '0531-86512036,18678896958', '', '山东职业学院西校区创建于2004年，是山东省人民政府举办、山东省教育厅主管的公办全日制院校——山东职业学院的重要组成部分，招生代码：Y059。山东职业学院西校区2017年普通高职招生计划为1000人，设有市场营销、会计、动漫制作技术、电子信息工程技术、计算机应用技术、软件技术、物流管理、财务管理、统计与会计核算、工程造价、商务英语、电子商务、法律事务、', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
