INSERT INTO `school` VALUES ('3154', '甘肃卫生职业学院', '甘肃省', '20万', '0', '62', '1956', '1050', '', '专科（高职）', '医药类', '专科（高职）', '公办', '', '{}', '甘肃', '兰州市', '', '', '甘肃省兰州市新区职教园区九龙江街1666号,五里铺校区：甘肃省兰州市城关区东岗西路60号', '730000', 'http://www.gswx.com.cn/list-167-1.htm', '0931-8265644', '', '甘肃卫生职业学院是经甘肃省人民政府批准设置、教育部备案的公办全日制普通高等学校。学校隶属甘肃省教育厅，其前身是创建于1956年的甘肃省卫生学校。学校是首批全国护理专业技能型紧缺人才示范性培训基地，是首批全国职业院校健康服务类示范专业点，是全国卫生职业教育教学指导委员会委员及护理分委会主任委员单位、中华医学会全国卫生职业教育研究会常务副主', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
