INSERT INTO `school` VALUES ('2973', '中国人民解放军陆军步兵学院', '解放军南京军区', '0', '0', '36', '1931', '0', '解放军南昌陆军学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '江西', '南昌市', '东湖区', '', '江西省南昌市', '', '', '0791-86871299,0791-86872299', '', '中国人民解放军陆军步兵学院，是培养新型陆军步兵初级指挥军官的高等教育院校。学院渊源于红军时期的红军学校，抗日战争时期的抗日红军大学、抗日军政大学，解放战争时期的东北军政大学、华中军政大学，从战火中走来，在战争中成长，上世纪50年代、80年代，先后有近千名优秀学员参加援越抗法和自卫反击作战，目前仍有4名具有参战经历的教授在教学岗位工作，先后', '0', '2', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
