INSERT INTO `school` VALUES ('2814', '首都师范大学继续教育学院', '北京', '0', '0', '11', '2000', '0', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '北京', '北京市', '海淀区', 'cnucrzsb@cnu.edu.cn', '北京市西三环北路56号(首师大北二区教学楼二层)', '100048', 'http://jxjyxy.cnu.edu.cn/zsxx/crgdjy/index.htm', '010-68901570', '', '首都师范大学建于1954年，办学历史可追溯至1905年成立的通州师范，是国家“双一流”建设高校、北京市与教育部“省部共建”高校。学校现有学科专业涵盖文、理、工、管、法、教育、外语、艺术等，六十多年来已培养各类高级专门人才二十余万名，是北京市人才培养的重要基地。学校现有博士学位授权一级学科18个，博士后流动站14个，硕士学位授权一级学科25个，1个博士', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2811', '澳门旅游学院', '', '0', '0', '82', '1995', '0', '澳门旅遊学院', '普通本科', '综合类', '普通本科', '公办', '', '{}', '澳门', '离岛', '嘉模堂区', 'admission@iftm.edu.mo', '澳门特别行政区澳门半岛望厦山', '', '', '853 2856 1252', '', '澳门旅游学院于1995年成立，为澳门公立高等院校，受澳门特别行政区政府社会文化司监督，提供旅游及服务业范畴的学位和专业培训课程。包括酒店、旅游、文化遗产、会展、零售及市场推广、休闲娱乐、康体活动、文化创意及厨艺等。此外，学院亦与国际知名院校合作开办高级管理课程。在调研方面，学院受特区政府及其他机构委托作旅游规划及发展的政策研究。澳门旅游', '1', '1', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2810', '澳门大学', '', '100万', '0', '82', '1981', '1635', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '澳门', '离岛', '嘉模堂区', 'admission@umac.mo', '澳门特别行政区氹仔大学大马路', '', 'http://www.umac.mo/', '853-88228833', '', '澳門大學是澳門唯一一所國際化綜合性公立大學，創立於1981年，具有多元文化共存的獨特優勢。實施國際化現代大學管理模式，八成教研人員來自世界各地，以英語授課為主，致力培養具有創新思維、家國情懷、國際視野、全球競爭力和世界擔當的人才。優質教學澳門大學設有人文學院、工商管理學院、教育學院、健康科學學院、法學院、社會科學學院、科技學院、榮譽學院', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2809', '澳门科技大学', '', '0', '0', '82', '2000', '0', '', '普通本科', '综合类', '普通本科', '民办', '', '{}', '澳门', '离岛', '嘉模堂区', 'enquiry@must.edu.mo', '澳门特别行政区氹仔岛伟龙马路', '', 'http://www.must.edu.mo/', '853-28881122', '', '澳門科技大學的五年策略規劃(2021-2025年)為大學的發展目標和重點發展方向制定了宏偉的藍圖。在重點發展方向中特別提出進一步加強工程學科建設以及鞏固優勢學科發展。我們生活在一個創新和技術高速發展的時代。在如此充滿挑戰的年代，為了成功培養未來的科技人才和領導者，重新調整學術和教育的目標，鞏固多學科、跨學科研發和教育的基石，已經成為全球教育工作者', '38', '23', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2700', '北京经济管理职业学院', '北京市', '0', '0', '11', '1979', '1287', '', '专科（高职）', '财经类', '专科（高职）', '公办', '', '{}', '北京', '北京市', '朝阳区', 'admin@biem.edu.cn', '望京校区：北京市朝阳区花家地街12号,固安校区：北京市京开高速永定河桥南800米路东', '100102', 'https://zsb.biem.edu.cn/', '010-64709690,010-64720773', '', '北京经济管理职业学院（北京经理学院）是北京市人民政府批准、教育部备案、北京市教育委员会主管的公办全日制普通高等职业学院。学校办学历史溯源于1979年成立的北京市经委职工教育师资进修学校，1984年更名为北京市经济管理干部学院；2003年，河北远东职业技术学院（原华北石油职工大学）划转并入北京市经济管理干部学院，同年，原国家经贸委在北京市经济管理干', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
