INSERT INTO `school` VALUES ('2696', '天津外国语大学滨海外事学院', '天津市教委', '40万', '0', '12', '2004', '500', '', '普通本科', '语言类', '普通本科', '民办', '', '{}', '天津', '天津市', '滨海新区', 'zhaoshengzx@sina.com', '天津市滨海新区大港学府路60号', '300270', 'http://bhws.tjfsu.edu.cn/zsxx/', '022-63250161,022-63257152', '', '天津外国语大学滨海外事学院是2004年经教育部批准设立，由天津外国语大学与天津天外教育投资有限公司按照新的机制和模式合作办学，从事全日制普通本科教育的独立学院，实行董事会领导下的院长负责制。学院坐落于天津市滨海新区，地处大港生态高教园内，文化气息浓厚，环境优美，交通便利。学院以外国语言文学为优势学科，文学、经济学、管理学、法学、教育学、', '0', '0', '0', '0', 0, '0', '0', '0', '0', '91.08', '4.34', '7.02', '956', '311', '24.55', '75.45', '2', '2');
