INSERT INTO `school` VALUES ('2692', '云南商务职业学院', '云南省教育厅', '0', '0', '53', '2010', '341', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '云南', '昆明市', '嵩明县', '', '云南省昆明市嵩明职教新城文苑路4号', '', 'https://www.ynbvc.com/subsiteIndex/cnzswPc.html', '0871-67977196,0871-67977197,0871-67977198', '', '云南商务职业学院(英文：YunnanBusinessVocationalCollege)，简称：“云商职院”。2010年经云南省人民政府批准，国家教育部备案，具有独立颁发国家承认学历文凭资格的全日制民办普通高等院校。学院位于兰茂故里，素有“滇东古镇”“鱼米之乡”美称的昆明市杨林职教园区，区域历史悠久，山川秀丽，沃土平畴，民风淳朴。学校占地340.69亩，总建筑面积26万平方米，总投资9.3亿。', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
