INSERT INTO `school` VALUES ('2690', '重庆旅游职业学院', '重庆市', '0', '0', '50', '2010', '312', '', '专科（高职）', '其他', '专科（高职）', '公办', '', '{}', '重庆', '重庆市', '黔江区', 'cqvitzjc@126.com', '重庆市黔江区舟白街道学府一路一号', '409099', 'http://www.cqvit.com/html/12/Index.shtml', '023-85086111,023-85086683', '', '重庆旅游职业学院成立于2010年3月，是重庆市人民政府设立、教育部备案的一所公办高职院校。学院地处“中国峡谷城武陵会客厅”重庆市黔江区，校园占地312亩，校舍面积14万余平方米。现有教职工400余人，其中有博士、硕士学位240余人，国家、市级工艺美术大师、非遗传承人、高技能人才50余人。学院面向中西部12个省、自治区、直辖市招生，在籍学生7000余人。学院以习近', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
