INSERT INTO `school` VALUES ('2671', '河南应用技术职业学院', '河南省', '0', '0', '41', '1956', '875', '河南化工职业学院,河南应用技术职业学院', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '河南', '郑州市', '', 'hnyyzsb@126.com', '河南省郑州市中原区建设西路548号', '450042', 'https://zsjy.hati.edu.cn/', '0371-67842026,0371-67842135,0371-67842209', '', '河南应用技术职业学院是隶属于河南省教育厅的一所全日制公办普通高等职业院校。现有郑州、开封两个校区，主校区在郑州市区。学校始建于1956年，前身是河南省化学工业学校，2010年转制为高等职业院校，2015年与原河南省医药学校合并更名为河南应用技术职业学院。学校是河南省省级骨干高职院校、省级品牌示范院校，省级优质高等职业院校，河南省高水平高等职业院校', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
