INSERT INTO `school` VALUES ('2629', '北京体育职业学院', '北京市', '0', '0', '11', '2008', '0', '', '专科（高职）', '体育类', '专科（高职）', '公办', '', '{}', '北京', '北京市', '丰台区', 'bjzgty@bjsports.gov.cn', '院本部：北京市丰台区光彩北路4号,分校区：北京市大兴区芦城乡政府路21号', '100075', 'http://www.bjtzhy.org/bjtzhy/zsjy/zsjz/index.html', '010-87280851', '', '北京体育职业学院是北京市人民政府批准建立、由北京市体育局于2008年6月创建的一所高等体育职业院校。属于全额拨款的事业单位，该院是在原北京市职工体育运动技术学院和北京市第二、第五体育运动学校基础上整合而成的。现有专业：竞技体育专业、运动训练专业（竞技体能训练方向）、社会体育专业（康体健身方向）、体育保健专业、体育服务与管理专业。在校生源：', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2628', '南宁理工学院', '广西壮族自治区教育厅', '0', '0', '45', '2002', '1038', '桂林工学院博文管理学院,桂林理工大学博文管理学院', '普通本科', '理工类', '普通本科', '民办', '', '{}', '广西', '南宁市', '武鸣区', 'zjb@bwgl.cn', '桂林校区：广西壮族自治区桂林市雁山区雁山镇雁山街317号,南宁校区：广西壮族自治区南宁市广西-东盟经济技术开发区发展大道16号', '', 'https://a.bwgl.cn/website/site/BLfY3bnqiiLMhWUS', '0771-6031666,0773-8998009', '', '南宁理工学院是教育部批准的独立设置的本科层次民办普通高等学校。学校前身是桂林理工大学博文管理学院，创办于2002年4月。学校现有东盟、桂林两个校区。东盟校区位于南宁教育园区，广西-东盟经济技术开发区，占地1038亩，规划总建筑面积69万平方米；桂林校区位于雁山教育园区，占地730亩，规划总建筑面积48万平方米。学校拥有现代化的教学楼，功能先进的实验室、实', '0', '0', '0', '0', 0, '0', '0', '0', '0', '94.74', '1.40', '0.13', '1283', '1719', '57.26', '42.74', '2', '2');
INSERT INTO `school` VALUES ('2626', '香港浸会大学', '', '0', '0', '81', '1956', '27', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '九龙半岛', '深水埗区', 'mainland@hkbu.edu.hk', '香港特别行政区九龙半岛塘窝打老道224号', '', 'https://admissions.hkbu.edu.hk/en/', '852-34112220', '', '香港浸会学院是由香港浸信会联会于1956年创办的私立高等学府，致力提供全人教育。1983年，当时的浸会学院获政府全面资助，成为公立专上学府，至1994年获得正名，成为香港浸会大学(浸大)。时至今日，浸大共设有八个学院，开办学士、副学士及高级文凭课程，以及修课式和研究式的硕士与博士课程。浸大对教学和研究同样重视，并鼓励教职员和学生服务社会。浸大以全人教', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
