INSERT INTO `school` VALUES ('2622', '海军工程大学', '解放军海军', '0', '0', '42', '1949', '2411', '解放军海军工程大学,中国人民解放军海军工程大学,中国人民解放军海军工程大学', '普通本科', '军事类', '普通本科', '公办', '', '{A-: 1, B: 1, B+: 1, B-: 1, C-: 1}', '湖北', '武汉市', '硚口区', '', '湖北省武汉市硚口区汉水桥街道解放大道717号', '', 'http://www.nue.edu.cn/', '027-83644884,027-65460090,027-65460093', '', '海军工程大学创办于1949年11月，1963年被确定为全国重点大学。学校坚持以本科教育与研究生教育为主体，学历教育与任职培训相结合。海军第一个本科专业、第一个硕士点、博士点、博士后科研流动站和全国重点学科均出自我校。学校是一所多学科、多层次、工程与管理、技术与指挥相结合的海军高等学府，是全军著名的多兵种专业类高等教育院校，是军队重要的教学科研基', '12', '7', '0', '0', 0, '92', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2620', '空军勤务学院', '解放军空军', '0', '0', '32', '2000', '1315', '解放军空军勤务学院,中国人民解放军空军勤务学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '江苏', '徐州市', '鼓楼区', '', '江苏省徐州市鼓楼区铜沛西阁街85号', '', '', '', '', '中国人民解放军空军勤务学院始建于1954年，是一所集空军后勤与装备指挥、技术、管理于一体，覆盖军事学、工学、经济学、管理学等多学科的高等军事院校，肩负培养能打胜仗高素质新型空军后勤与装备保障人才的重要任务。1954年10月，空军勤务学院在山西太原组建成立，在五十多年的发展历程中，学院先后数易其名，经过了初建、重建、调整前进和快速发展四个阶段。2003', '1', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2619', '香港岭南大学', '', '0', '0', '81', '1888', '0', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '新界', '屯门区', 'MLadm@LN.edu.hk', '香港新界屯门青山公路八号', '', 'https://www.ln.edu.hk/admissions/ug/mainland/news', '852-26168750', '', '岭南大学（岭大）是香港的博雅大学，在本地高等院校中历史最悠久。岭大的历史可追溯至1888年其前身「格致书院」在广州创校开始，其后於1967年以岭南书院之名在香港复校。2015年，岭大被《福布斯》评为「亚洲十大顶尖博雅学院」之一。岭大提供的本科生课程、修课式研究生课程、哲学硕士/博士学位课程和涵盖人文学、商学和社会科学的范畴，全面的课程内容包括通识及', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
