INSERT INTO `school` VALUES ('2616', '内蒙古科技大学包头医学院', '内蒙古自治区教育厅', '64万', '0', '15', '1958', '1050', '', '普通本科', '医药类', '普通本科', '公办', '', '{}', '内蒙古', '包头市', '东河区', 'kdby19127@eyou.com', '内蒙古自治区包头市东河区建设路31号', '014040', 'http://www.btmc.cn/zsxxw/', '0472-7167734', '', '包头医学院成立于1958年，在如歌岁月中，一代代包医人秉承“团结、敬业、求实、创新”的校训，同心致远、聚力前行，以热血丹心为国家育医学人才，在祖国边疆播撒卫生健康的希望。作为新中国在边疆民族地区较早建立的一所普通本科高等医学院校，包头医学院在建校当年即招收五年制临床医学专业本科学生。1959年和1961年，包头市卫生学校和内蒙古卫生干部进修学院先', '5', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
