INSERT INTO `school` VALUES ('2566', '兴义民族师范学院', '贵州省', '0', '0', '52', '1813', '1211', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '贵州', '黔西南布依族苗族自治州', '', 'zsjy@xynun.edu.cn', '中心校区：贵州省黔西南布依族苗族自治州顶效开发区兴义路1号,市内校区：贵州省黔西南布依族苗族自治州湖南街32号,安龙校区：贵州省黔西南布依族苗族自治州安龙县杨柳街18号', '562400', 'http://zsjy.xynun.edu.cn/zhaosheng1/index.html', '0859-3236966', '', '兴义民族师范学院肇基于1813年重建的笔山书院，历经高等小学堂、兴义县立中学、贵州省兴义中学等历史时期。建国后，于1952年改为贵州省兴义师范学校，开始了现代师范教育的历史。1975年名为兴义地区“五·七”师范大学，1978年改称兴义师范专科学校，1982年改名黔西南民族师专，1993年又更名为黔西南民族师范高等专科学校。2001年开始与贵州师范大学联合办学培养本科学', '0', '0', '0', '0', 557, '490', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
