INSERT INTO `school` VALUES ('2421', '厦门工学院', '福建省教育厅', '0', '0', '35', '2009', '1304', '华侨大学厦门工学院', '普通本科', '理工类', '普通本科', '民办', '', '{}', '福建', '厦门市', '集美区', 'zsb@xit.edu.cn', '福建省厦门市集美区孙坂南路1251号', '361021', 'https://www.xit.edu.cn/zsw/', '0592-6667566', '', '厦门工学院是2009年经国家教育部批准设立的一所普通本科高校，2019年通过教育部本科教学工作合格评估。学校座落在中国著名的海上花园城市——福建省厦门市，人文环境优美，四季气候宜人。海、陆、空交通便捷，是最适合人的居住、读书、研究、就业、创业的好地方。厦门工学院被评选为“全国最美校园”，实至名归。学校建成了公园式的校园，繁花芬芳、鸟语花香，文', '0', '0', '0', '0', 0, '0', '0', '0', '0', '97.05', '1.09', '1.71', '1168', '1406', '55.00', '45.00', '2', '2');
