INSERT INTO `school` VALUES ('2215', '江阴职业技术学院', '江苏省', '0', '0', '32', '1979', '695', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '无锡市', '', 'jy86022665@163.com', '江苏省无锡市江阴锡澄路168号', '214405', 'http://zsb.jypc.edu.cn/', '0510-86028266,0510-86028267', '', '江阴职业技术学院是长江下游一座经济发达的美丽的江南名城——江阴的一所公办普通高职院校。江阴，犹如滚滚长江玉带旁的一顶“皇冠”，熠熠生辉，令人瞩目。这座现代化滨江花园城市经济总量连年位居江苏省县（市）之首，实现了自2003年以来全国县域经济基本竞争力“十八连冠”。目前，全市拥有3万多家企业，其中11家企业跻身“中国企业500强”，15家企业上榜“中', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2214', '常州机电职业技术学院', '江苏省', '0', '0', '32', '1963', '842', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '常州市', '', '1032@czmec.cn', '江苏省常州市武进区鸣新中路26号', '213164', 'http://www.czimt.edu.cn/zsw/main.psp', '0519-86331666', '', '常州机电职业技术学院，是一所省属公办全日制普通高等学校，隶属于江苏省教育厅。学校创办于1963年，原名常州市机械职业学校，2002年6月独立升格，现坐落于常州科教城。学校始终秉持“知行并进”的校训精神，坚持“以人为本、崇尚技术、开放共享、追求卓越”办学理念，坚持“敬业爱生言传身教”的教风、“谦虚好学严谨创新”的学风，根植智能装备制造产业，推进', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
