INSERT INTO `school` VALUES ('2207', '泰州职业技术学院', '江苏省', '100万', '0', '32', '1958', '1082', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '泰州市', '海陵区', 'zsbtzy@163.com', '江苏省泰州市医药高新区天星路8号', '225300', 'http://zs.tzpc.edu.cn/', '0523-86055000,0523-86055001,0523-86055002', '', '泰州职业技术学院于1998年3月经原国家教委批准建立，是全国第一批14所全日制高等职业技术院校之一，是江苏省第一所冠以“职业技术学院”名称的院校，也是地级泰州市第一所普通高校。学院隶属于泰州市人民政府。学院办学历史可追溯到1958年成立的泰州医学专科学校（后更名为泰州卫生学校）；1985年6月，三所行业局办职工大学合并成立泰州市职工大学；1998年3月，成立', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2206', '沙洲职业工学院', '江苏省', '10万', '0', '32', '1984', '530', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '苏州市', '', 'xsc@szit.edu.cn', '江苏省苏州市张家港市福新路1号', '215600', 'https://zs.szit.edu.cn/website/', '0512-56730120', '', '底蕴深厚的办学历史：沙洲职业工学院是一所公办普通高等学校，创建于1984年，作为全国第一所县办大学，被载入中国高等教育史。时任中共中央总书记胡耀邦寄语“沙工有如扬子水，不尽人才滚滚来。”著名科学家、教育家钱伟长自开办之初一直担任名誉院长，时任全国人大副委员长胡厥文题写校名，知名社会学家费孝通提出“勤奋、求实、开拓、进取”的校训。•独具优', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
