INSERT INTO `school` VALUES ('2203', '连云港职业技术学院', '江苏省', '0', '0', '32', '1979', '1150', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '连云港市', '', 'lygtczjb@126.com', '江苏省连云港市晨光路2号', '222000', 'http://www.lygtc.edu.cn/zsxxw/', '0518-85985555,0518-85985678', '', '连云港职业技术学院前身是连云港职业大学，1983年3月经教育部批准成立；1999年3月，连云港职业大学与连云港市职教中心合并，更名为连云港职业技术学院。建院之初，学院与全国一百多所职业大学率先举起了“高等职业教育”的旗帜，实现了“办起来”的历史性飞跃。2008年，学院搬入连云港市科教园区，建成了千亩美丽校园，办学条件和资源发生了根本性变化，铸就了“大', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2202', '苏州工艺美术职业技术学院', '江苏省', '10万', '0', '32', '1958', '561', '', '专科（高职）', '艺术类', '专科（高职）', '公办', '', '{}', '江苏', '苏州市', '', 'zsjy@sgmart.edu.cn', '江苏省苏州市吴中大道国际教育园南区致能大道189号', '215104', 'http://zsjy.sgmart.edu.cn/', '0512-66508089,0512-66508090,0512-66508099', '', '苏州工艺美术职业技术学院是我国成立的第一所艺术设计高等职业院校，前身为1958年8月创办的“苏州工艺美术专科学校”。1999年，苏州工艺美术学校与苏州轻工职工大学联合组建苏州工艺美术职业技术学院，成为我国第一所独立设置的艺术设计高等职业院校。2006年，学校在高职高专人才培养水平评估中获得优秀成绩，2007年，成为首批“江苏省示范性高等职业院校”。2010年', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2201', '镇江市高等专科学校', '江苏省', '0', '0', '32', '1992', '1000', '', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '江苏', '镇江市', '', 'gzzb@zjc.edu.cn', '江苏省镇江市高校园区长香西大道518号', '212028', 'http://zsw.zjc.edu.cn/', '400-8881410,0511-88962859', '', '镇江市高等专科学校，是1992年经教育部批准，由原镇江教育学院、镇江市职业大学和江苏省广播电视大学镇江分校合并成立的一所综合性公办普通高校，是教育部人才培养工作水平评估优秀学校，办学渊源可以追溯到1912年我国职业教育先行者、著名国画大师、教育家吕凤子先生创办的正则女校，已有百余年办学历史。学校地处著名历史文化名城——江苏省镇江市，现有主校区', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2200', '哈尔滨城市职业学院', '黑龙江省教育厅', '0', '0', '23', '1993', '240', '黑龙江北开职业技术学院，哈尔滨江南职业技术学院', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '黑龙江', '哈尔滨市', '阿城区', '', '黑龙江省哈尔滨市新华开发区城市职业学院', '150317', '', '0451-55697777,0451-82916666', '', '哈尔滨城市职业学院是经黑龙江省政府批准，哈尔滨市人民政府主办，国家计划内统招党办高等院校。学校位于哈尔滨市，坐落在国家著名旅游区金上京新华开发区，是全国首家加入中国标准化协会担任常务理事并参与制定教育教学标准的高校，各项教育教学活动均达到国家标准，学校办学基础雄厚，校园文化气息浓郁。学校教学占地面积16万平方米，教学实训基地占地面积425', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2199', '哈尔滨应用职业技术学院', '黑龙江省教育厅', '0', '0', '23', '2005', '372', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '黑龙江', '哈尔滨市', '', 'hyyzy@126.com', '黑龙江省哈尔滨市道里区机场路998号', '150078', 'http://hayingyong.1zhanyun.com/', '0451-84114766,0451-84116658', '', '哈尔滨应用职业技术学院是黑龙江省人民政府批准，哈尔滨市人民政府主管，国家教育部备案、独立设置、国家计划内统一招生，具有独立颁发国家承认学历文凭资格的全日制综合性普通高等院校。现已成为一所以工科类学科为特色，多学科协调发展，着眼于培养区域经济社会发展急需的高素质技术技能人才的高等院校。学院现有护理学院及汽车工程系等八个院系部，开设护', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
