INSERT INTO `school` VALUES ('2194', '七台河职业学院', '黑龙江省', '0', '0', '23', '2005', '405', '', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '黑龙江', '七台河市', '桃山区', 'zsb168@126.com', '黑龙江省七台河市桃山区学府街229号', '154600', '', '0464-8687508,0464-8687358', '', '七台河职业学院、七台河技师学院分别是经省政府批准，教育部、人力资源和社会保障部备案的公办全日制普通高等职业和技工院校。两院坐落于东北边陲、冬奥冠军摇篮之乡的国家园林城市——黑龙江省七台河市。市中心区主干道学府街横贯校园，形成一条街道南北两校区的格局。2013年,七台河市委市政府将两所院校资源整合共建，对内统称“七台河职业技师学院”,实行党', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2193', '黑龙江公安警官职业学院', '黑龙江省', '0', '0', '23', '1946', '720', '', '专科（高职）', '政法类', '专科（高职）', '公办', '', '{}', '黑龙江', '哈尔滨市', '', '', '黑龙江省哈尔滨市呼兰区利民开发区学院南路333号', '150025', '', '0451-88126111,0451-88126210', '', '黑龙江公安警官职业学院（黑龙江省公安厅人民警察训练中心）是黑龙江省唯一一所培养公安专门人才的高等职业院校，也是黑龙江省公安中级警官的培训基地。学院始建于1946年，其前身是黑龙江省政法干部学校和黑龙江省人民警察学校。1978年，开始中专学历教育。1984年至1989年，举办痕迹检验和刑事侦查专业专科班。1998年至2003年，与黑龙江大学合作办学，成立黑龙江大学', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2192', '黑龙江幼儿师范高等专科学校', '黑龙江省', '45万', '0', '23', '1906', '264', '', '专科（高职）', '师范类', '专科（高职）', '公办', '', '{}', '黑龙江', '牡丹江市', '', 'hyzzsjy@163.com', '黑龙江省牡丹江市爱民区西地明街7号', '157011', 'http://zs.hljys.cn', '0453-6559138,0453-6559326', '', '黑龙江幼儿师范高等专科学校是国家教育部于2007年3月16日正式批准成立的一所公办普通高等院校。学校的历史可以追溯至1906年（清光绪三十二年）清政府在宁古塔（现宁安市）两等小学附设的师范学堂。1990年以后，牡丹江师范学校、宁安师范学校、牡丹江幼儿师范学校三所学校合并，在此基础上建立黑龙江幼儿师范高等专科学校，发展成为独具特色的高等学府，是全国最早', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2191', '大庆医学高等专科学校', '黑龙江省', '0', '0', '23', '1965', '369', '', '专科（高职）', '医药类', '专科（高职）', '公办', '', '{}', '黑龙江', '大庆市', '', '', '黑龙江省大庆市卡尔加里路11号', '163312', 'http://www.dqygz.com/zsw/', '0459-5885647,0459-5816639,18945620810', '', '大庆医学高等专科学校是大庆市所属的一所公办普通高校，其前身大庆石油管理局卫生学校建于1965年，大庆职工医学院于1985年在卫生学校基础上成立。2002年8月，学校开始在大庆职工医学院（大庆石油管理局卫生学校）基础上申办大庆医学高等专科学校，2006年2月经国家教育部批准成立，成为黑龙江省一所独立设置的医学高等专科学校。2019年6月18日，学校从油田公司划转大庆', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2190', '齐齐哈尔高等师范专科学校', '黑龙江省', '0', '0', '23', '1906', '319', '', '专科（高职）', '师范类', '专科（高职）', '公办', '', '{}', '黑龙江', '齐齐哈尔市', '', '124062925@qq.com', '黑龙江省齐齐哈尔市民航路566号', '161005', 'http://www.qqhrtc.com/zsw/', '0452-2409077', '', '齐齐哈尔高等师范专科学校前身为始建于1906年（清光绪三十二年）的省立第一师范学校，坐落在被誉为“世界大湿地，中国鹤家乡”的国家重要装备工业基地、中国绿色食品之都——齐齐哈尔，素有“龙江师范第一校”的美誉。2003年，经教育部批准，将原齐齐哈尔市幼儿师范学校、市教育学院和1947年毛泽东同志亲笔题名“萌芽学校”的克山师专部分资源融入，建成全日制普', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2189', '哈尔滨体育学院', '黑龙江省', '0', '0', '23', '1956', '4172', '', '普通本科', '体育类', '普通本科', '公办', '', '{}', '黑龙江', '哈尔滨市', '', '', '黑龙江省哈尔滨市南岗区大成街1号', '150008', '', '0451-82766447,0451-82713479', '', '哈尔滨体育学院创办于1956年，现已建设成为冰雪特色鲜明，学科布局合理，专业协调发展，教学、科研竞赛训练和社会服务成绩卓著的高等体育学府，是我国冰雪运动人才培养的主要基地，是教育部本科教学工作水平评估优秀院校，是体育学一级学科博士学位授权单位。学校隶属于黑龙江省教育厅，各类全日制在校生6000余人。学校总占地面积278.10万平方米，建筑面积17.48万平', '1', '0', '0', '0', 0, '389', '696', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2188', '云南科技信息职业学院', '云南省教育厅', '0', '0', '53', '2001', '880', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '云南', '昆明市', '盘龙区', 'zhaoban@ynkexin.cn', '云南省昆明市穿金路757-1号', '650224', '', '0871-5015065', '', '云南科技信息职业学院是云南省第一所经省政府批准、国家教育部备案，面向全国招生，具有独立颁发国家高等专科学历文凭的全日制民办普通高等职业学院，始建于2001年，是在当时云南省高等教育毛入学率远低于全国平均水平的情况下，由省委、省政府、省政协、省教育厅刚退下来的一批老领导以及云南大学、云南师范大学、昆明理工大学、云南财经学院、昆明医学院等11', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
