INSERT INTO `school` VALUES ('2184', '云南经济管理学院', '云南省教育厅', '0', '0', '53', '1992', '1187', '云南经济管理职业学院', '普通本科', '财经类', '普通本科', '民办', '', '{}', '云南', '昆明市', '安宁市', 'ynjgyzs@126.com', '云南省昆明市五华区海屯路296号', '650106', 'http://zs.ynjgy.com/', '0871-68314770', '', '云南经济管理学院是经教育部批准设立的一所全日制普通本科院校。学校创办于1992年，是云南省办学最早的民办学校之一。学校位于春城昆明，现有安宁、海源两个校区，教学行政用房52.5万平方米，教学科研仪器设备值2.15亿元，馆藏纸质图书305.85万册、电子图书160万册，建成了高速、稳定、便捷的校园网络，实现“万兆到楼宇、百兆到桌面、无线全覆盖”。建校29年来，学校', '0', '0', '0', '0', 0, '0', '0', '0', '0', '98.18', '3.02', '0.04', '2477', '2085', '46.00', '54.00', '2', '2');
