INSERT INTO `school` VALUES ('2157', '昭通学院', '云南省', '82万', '0', '53', '1978', '820', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '云南', '昭通市', '', '', '云南省昭通市昭阳区国学路', '657000', 'http://zjc.ztu.edu.cn/', '0870-2121914,0870-8468666', '', '昭通学院是一所省属公办普通本科高校，位于云、贵、川三省交界、素有“咽喉西蜀，锁钥南滇”的云南省昭通市。学校环境优美，毗邻占地一千余亩的省耕国学文化公园，春观花，夏品文，秋赏月，冬戏雪。昭通学院文脉传承，底蕴丰厚，其师范教育传统可追溯到1906年清政府创设的师范传习所，历经省立第二师范学校、昭通地区师范学校，1978年成立昭通师范专科学校，2012', '0', '0', '0', '0', 581, '509', '0', '0', '0', '96.26', '1.16', '0.00', '728', '395', '35.17', '64.83', '2', '2');
