INSERT INTO `school` VALUES ('2137', '曲阜远东职业技术学院', '山东省教育厅', '0', '0', '37', '1998', '400', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '山东', '济宁市', '曲阜市', 'qfydzsb@126.com', '曲阜校区：山东省曲阜市石门山风景区,邹城校区：山东省济宁市邹城市职教园区,曲阜高铁校区：山东省曲阜市曲阜东高铁站西200米孔子大道1号', '273115', 'https://www.fareast-edu.net/dept-zsxxw', '0537-7383999,0537-7023999,0537-4507999', '', '曲阜远东职业技术学院是于1998年7月兴办的一所普通高校。1999年6月经省政府批准与山东农业大学合作成立远东工商外语学院。2000年10月，经省政府批准、国家教育部备案，建立曲阜远东职业技术学院。2016年7月，学院在孟子故里--邹城市职教园区，设立分校区，占地面积400余亩，2020年8月计划启用曲阜高铁新校区。2020年9月，启用曲阜远东职业技术学院高铁校区学校管理实行院', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
