INSERT INTO `school` VALUES ('2092', '四川文化传媒职业学院', '四川省教育厅', '0', '0', '51', '1998', '450', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '四川', '成都市', '', 'whcmxy5188@163.com', '崇州校区：四川省成都市崇州市学苑东路22号,绵竹校区：四川省绵竹市二环路东路段88号', '611230', 'http://www.svccc.net/ZSW_Index', '028-82312000,028-82386271', '', '四川文化传媒职业学院是一所经四川省人民政府批准、国家教育部备案、纳入国家计划内统一招生、具有独立颁发国家承认大学专科学历文凭资格的全日制普通高等职业院校，成立于2005年，2015年顺利通过人才培养工作现场评估。学院占地面积450余亩，主校区座落于具有2200多年历史的文化名城――崇州；分校区建在有“中国年画之乡”和“中国三大名酒之一剑南春”产地——', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('2091', '四川华新现代职业学院', '四川省教育厅', '0', '0', '51', '2005', '500', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '四川', '成都市', '', 'zjc@schxmvc.com.cn', '西河校区：四川省成都市龙泉驿区成洛大道3833号,机投校区：四川省成都市武侯区武青北路7号', '610107', 'http://www.schxmvc.com.cn/zsjy.htm', '028-84811011', '', '四川华新现代职业学院是经四川省人民政府批准、教育部备案的全日制全国计划内统一招生的综合应用型普通高等院校。学院初创于2005年，经四川省人民政府批准，四川开放大学（原四川广播电视大学）于2007年全面接办华新学院。2011年，学院顺利通过教育部高职高专人才培养工作评估。学院坚持规模、质量、效益的有机统一，不断深化教学改革，办学实力和水平逐年提升。', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
