INSERT INTO `school` VALUES ('1957', '江汉艺术职业学院', '湖北省', '0', '0', '42', '1959', '1000', '', '专科（高职）', '艺术类', '专科（高职）', '公办', '', '{}', '湖北', '省直辖县级行政区划', '潜江市', 'hbjhart@263.net', '湖北省潜江市师范路16号', '433100', 'https://www.hbjhart.cn/zsxxw/', '0728-6239001', '', '江汉艺术职业学院是2004年经湖北省人民政府批准成立、教育部备案的公办全日制普通高等学校，前身为湖北省潜江艺术师范学校，创建于1959年。学院坐落于素有“曹禺故里、江汉油城、水乡园林、龙虾之乡”美誉的湖北省潜江市，占地面积1000亩，校舍面积20万平方米，馆藏图书45万册。学院设8个教学院部：音乐学院、设计学院、学前教育学院、管理学院、表演艺术学院、潜江', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1956', '咸宁职业技术学院', '湖北省', '0', '0', '42', '1965', '1086', '', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '湖北', '咸宁市', '', 'xjzb9788@163.com', '湖北省咸宁市咸宁大道118号', '437100', 'http://zhaoban.xnec.cn', '0715-8259788,0715-8259819', '', '咸宁职业技术学院是由五所学校合并经湖北省人民政府批准、教育部备案的一所综合性公办高职院校。前身之一为1976年成立的华中农学院咸宁分院，最早开办的是1965年咸宁财贸工商管理学校。1999年被省政府列为“全省首批试点高职院校”。2022年获批立项湖北省“双高”学校。我校位于中国香城泉都——湖北省咸宁市，是武汉、长沙、南昌三大省会城市经济区的地理中心，北', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
