INSERT INTO `school` VALUES ('1947', '武汉船舶职业技术学院', '湖北省', '0', '0', '42', '1950', '600', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '湖北', '武汉市', '', 'zhaoban@mail.wspc.edu.cn', '湖北省武汉市汉阳区月湖街铁桥南村2号', '430050', 'http://zsw.wspc.edu.cn/', '027-84803777,027-84803666', '', '学院概况武汉船舶职业技术学院溯源于1950年中南兵工局创办的“中南兵工学校”，是新中国创办的首批军工学校。学院历经中南兵工学校、湖北工学院等7个时期，先后隶属于中南兵工局、六机部、中国船舶工业总公司等，1998年成为教育部首批设置的全日制高等职业学校，1999年转由湖北省人民政府主办。学院坐落在素有“九省通衢”之称的武汉市中心城区，南濒月湖，北临汉', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1946', '湖北工业职业技术学院', '湖北省', '0', '0', '42', '1966', '1000', '十堰职业技术学院', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '湖北', '十堰市', '', 'hgzyzb@163.com', '湖北省十堰市北京中路38号', '442000', 'http://zb.hbgyzy.edu.cn/', '0719-8126013,0719-8126015,0719-8126024', '', '湖北省十堰市近依道教胜地武当山，毗邻华中屋脊神农架和南水北调中线水源地丹江口水库。这是一块神奇的地方，距今100万年前，这里就有古人类产生，“郧县人”的发现，改变人类起源的一元说。这是一片创业的热土，50年前，中国第二汽车制造厂（现东风汽车集团有限公司）在这里诞生，成为了闻名中外的汽车城。湖北工业职业技术学院坐落于拥有“仙山、秀水、汽车城', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1945', '荆州理工职业学院', '湖北省', '0', '0', '42', '1983', '690', '', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '湖北', '荆州市', '', 'hbssdx@163.net', '湖北省荆州市沙市区工农路22号', '434000', 'http://www.jzlg.cn/zs/', '0716-8252882,0716-8517410', '', '荆州理工职业学院于1983年经湖北省人民政府批准成立，是教育部备案的公办全日制普通高等学校，座落在人文荟萃的历史文化名城——荆州，以工学为主，涵盖经济、管理、艺术等专业大类。学校办学历史悠久、校风严谨、管理规范、特色鲜明。学校坚持“立足荆州、面向湖北、辐射全国、凸显理工、培育工匠”的办学定位，秉承“以人为本、注重质量、突出特色、服务社会', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
