INSERT INTO `school` VALUES ('1900', '厦门演艺职业学院', '福建省教育厅', '7万', '0', '35', '2002', '200', '', '专科（高职）', '艺术类', '专科（高职）', '民办', '', '{}', '福建', '厦门市', '', 'zsb@xmyanyi.com', '福建省厦门市翔安区翔安东路1995号', '361101', 'https://www.xmyanyi.com/index.php?c=category&id=10', '0592-7768817,0592-7768816,0592-7768815', '', '厦门演艺职业学院是经福建省人民政府批准、教育部备案具有独立颁发学历文凭资质的全日制普通高等职业院校。学院2002年4月在厦门风景优美的鼓浪屿开始筹建，于2004年2月正式成立（国标代码：13767）。2009年9月，学院整体迁至厦门市翔安区春波路7号，作为新校区正式投入使用前的过渡校区。2019年3月，学院正式落户于厦门市翔安区翔安东路1995号，新校区占地200亩，总规划', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1899', '福建华南女子职业学院', '福建省教育厅', '0', '0', '35', '1908', '234', '', '专科（高职）', '其他', '专科（高职）', '民办', '', '{}', '福建', '福州市', '', '707406393@qq.com', '福建省福州市闽侯县大学城学府南路66号', '350100', 'http://www.hnwomen.com/zsinfo/zs/', '0591-87429966', '', '福建华南女子职业学院前身追溯于1908年的私立华南女子文理学院，复办于1984年11月，是建国后第一所具有独立颁发学历文凭资格的民办女子高等学校，是教育部非营利民办高校办学制度改革试点项目单位。学院坐落于风景优美的旗山东麓，是唯一入驻大学城的民办高职院校。学院复办以来，得到省市政府的大力支持。习近平、贾庆林、贺国强、李岚清、陈慕华、雷洁琼、陈明', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1898', '泉州纺织服装职业学院', '福建省教育厅', '0', '0', '35', '2001', '350', '', '专科（高职）', '综合类', '专科（高职）', '民办', '', '{}', '福建', '泉州市', '', '626064513@qq.com', '福建省泉州市石狮市学府路1358号', '362700', '', '0595-36107666,0595-36107788', '', '泉州纺织服装职业学院创办于2001年，是教育部备案的具有独立颁发高等教育文凭资格的全日制普通高等院校,也是福建省唯一一所以“纺织服装”命名的高职院校。学院位于泉州石狮，占地面积350亩，现有教职工300余人，全日制在校生5000余人。学院以“特色化办学、规范化管理、为地方经济服务、让每个学子成才”为办学理念，以培养“地方离不开、行业都认同、国际能交流', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1897', '厦门华厦学院', '福建省教育厅', '75万', '0', '35', '1993', '600', '', '普通本科', '综合类', '普通本科', '民办', '', '{}', '福建', '厦门市', '', '', '福建省厦门市集美文教区天马路288号', '361024', 'http://zsb.hxxy.edu.cn', '0592-6276222,0592-6279222', '', '厦门华厦学院是教育部批准设立的本科层次的普通高校，是一所有着20多年办学历史、特色鲜明、社会声誉良好的应用技术型大学。学校地处著名海上花园城市——厦门，毗邻沿海高铁枢纽站——厦门北站，人流、物流、商流、信息流汇聚。厦门华厦学院于1993年在时任厦门市政协主席蔡望怀的倡导下，由厦门市政协发动社会力量捐资创办，是厦门市委、市政府重点支持的民办', '0', '0', '0', '0', 0, '0', '0', '0', '0', '99.45', '23.29', '0.07', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1896', '福州英华职业学院', '福建省教育厅', '23万', '0', '35', '1881', '240', '', '专科（高职）', '语言类', '专科（高职）', '民办', '', '{}', '福建', '福州市', '鼓楼区', '478769935@qq.com', '福建省福州市闽侯荆溪镇荆港路128号', '350018', 'http://www.fzacc.com/', '0591-62098082', '', '福州英华职业学院溯源于1881年创建的“英华书院”(俗称“英华斋”)，迄今已有141年的办学历史，在学院百余年的教育史上，培养了一代又一代优秀人才。在辛亥革命时期有着重要社会影响的前国民主席林森、爱国华侨黄乃裳等一批杰出人才出自英华，为中国革命做出杰出贡献的中共福州地委第一任书记方尔灏等40位革命烈士也来自英华，而当代“中国化工之父”侯德榜、动力', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1895', '闽南理工学院', '福建省教育厅', '84万', '0', '35', '1998', '1100', '', '普通本科', '理工类', '普通本科', '民办', '', '{}', '福建', '泉州市', '', 'mnlgzsb@163.com', '福建省泉州市石狮宝盖山风景区', '362700', 'http://zs.mnust.edu.cn/', '0595-83777777,0595-83677777', '', '闽南理工学院创办于1998年，经国家教育部批准于2008年升格为普通本科高等学校。2012年获学士学位授予权单位。2016年通过教育部本科教学工作合格评估。2018年1月，我校被福建省教育厅确定为硕士学位授予培育单位立项建设高校。学校位于我国历史文化名城、著名侨乡、“海上丝绸之路”起点——福建省泉州市，坐落在台湾海峡西岸、滨海城市石狮宝盖山风景区，校园占地面', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
