INSERT INTO `school` VALUES ('1866', '闽西职业技术学院', '福建省', '60万', '0', '35', '1983', '844', '', '专科（高职）', '综合类', '专科（高职）', '公办', '', '{}', '福建', '龙岩市', '', '2064759852@qq.com', '福建省龙岩市闽大路8号', '364030', 'http://zs.mxdx.net', '0597-2589999,0597-2587777,0597-2751453', '', '闽西职业技术学院是由龙岩市委、市政府举办的龙岩唯一的一所高职高专院校。学校前身为闽西职业大学，历史上简称“闽大”。2004年，龙岩工业学校并入闽西职业大学，更名为闽西职业技术学院。2019年，龙岩市委、市政府推进市职业教育资源整合，龙岩卫校、龙岩财校整体并入闽西职业技术学院。学校拥有校本部、天马校区、虎岭校区、西湖岩校区4个校区，占地56.24公顷，', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
