INSERT INTO `school` VALUES ('1862', '厦门医学院', '福建省', '43万', '0', '35', '1900', '885', '厦门医学高等专科学校', '普通本科', '医药类', '普通本科', '公办', '', '{}', '福建', '厦门市', '', '201000010152@xmmc.edu.cn', '福建省厦门市集美区灌口中路1999号', '361023', 'https://www.xmmc.edu.cn/zsxx/', '0592-6275566,0592-6375566', '', '厦门医学院是一所全日制公办普通本科高校，是福建省三所医学类本科高校之一，也是闽西南唯一的医学类本科高校。办学渊源最早可追溯到1898年在鼓浪屿创设的医学专门学校。学校传承“德厚立人、术湛立业”的校训，凝练并弘扬“阳光、大气、善良、感恩”的校风和“忠诚、团结、实干、创新”的学风，以培养“对国家尽忠、对父母尽孝、对单位尽责、对患者尽心”的“', '0', '0', '0', '0', 0, '512', '0', '0', '0', '99.91', '7.87', '0.09', '793', '274', '25.68', '74.32', '2', '2');
