INSERT INTO `school` VALUES ('1778', '浙江警官职业学院', '浙江省', '0', '0', '33', '1984', '524', '', '专科（高职）', '政法类', '专科（高职）', '公办', '', '{}', '浙江', '杭州市', '江干区', 'zs@zjjy.com.cn', '浙江省杭州市下沙高教园区二号大街', '310018', 'https://www.zjjy.com.cn/557/list.htm', '0571-86878161', '', '浙江警官职业学院是一所司法警官院校，隶属浙江省司法厅，主要培养监狱戒毒人民警察、人民法院检察院司法警察、基层司法行政工作者，以及法律服务、安全防范、应急管理等领域高素质技术技能人才，下设浙江省司法行政培训中心、浙江省司法行政研究院、汽车驾驶培训中心。学院前身为1982年8月创办的省劳改工作干部训练班。1984年，经浙江省政府批准正式建立浙江省', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
