INSERT INTO `school` VALUES ('1727', '上海工商外国语职业学院', '上海市教委', '0', '0', '31', '2001', '320', '', '专科（高职）', '语言类', '专科（高职）', '民办', '', '{}', '上海', '上海市', '浦东新区', '', '上海市浦东新区惠南镇观海路505号', '201300', 'https://zs.sicfl.edu.cn/', '021-68020551,021-68021818,021-68020632', '', '上海工商外国语职业学院创建于2001年，坐落于浦东航空港与洋山深水港之间，是经上海市人民政府批准、国家教育部备案的民办全日制普通高等院校。现有在校生近9000人，是上海市办学规模最大的民办高职院校之一。学校遵循党的教育方针，适应国家改革开放，为区域经济建设服务，立足上海，面向长三角，辐射中西部地区，以培养语言类应用型人才和涉外服务复合型人才为', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
