INSERT INTO `school` VALUES ('1724', '上海电影艺术职业学院', '上海市教委', '0', '0', '31', '2003', '65', '', '专科（高职）', '艺术类', '专科（高职）', '民办', '', '{}', '上海', '上海市', '浦东新区', 'sfaa50271101@126.com', '上海市浦东新区达尔文路188号', '', '', '021-50273668,021-50392087', '', '上海电影艺术职业学院成立于2003年12月，是经国家教育部和上海市人民政府批准的全日制普通高等艺术院校，坐落于上海市浦东新区张江高科技园区内。作为全国第二所以电影命名的独立院校，学院秉承“厚德、博艺、创新、和谐”的校训，抓住上海推进电影产业繁荣与复兴的契机，聚集现代电影工业，注重艺术与科技的结合，全面推行产教融合协同育人和国际化发展战略，', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
