INSERT INTO `school` VALUES ('1720', '兰州资源环境职业技术大学', '甘肃省', '74万', '0', '62', '1951', '2307', '兰州财经大学长青学院,兰州资源环境职业技术学院', '普通本科', '理工类', '普通本科', '公办', '', '{}', '甘肃', '兰州市', '城关区', 'zsgzc@lzre.edu.cn', '校本部：甘肃省兰州市城关区窦家山36号,西校区：甘肃省兰州市城关区段家滩路1173号', '730021', 'http://zsc.lzre.edu.cn/', '0931-8799489,0931-8799137', '', '兰州资源环境职业技术大学是整合兰州财经大学长青学院、兰州资源环境职业技术学院办学资源，经教育部批准、甘肃省政府同意设置的公办本科层次职业大学。学校前身是隶属于中国气象局的原国家重点中专兰州气象学校（始建于1951年）和隶属于甘肃省煤炭工业局的甘肃煤炭职工大学（始建于1984年）。2004年经教育部备案、甘肃省政府批准，两校合并改建为我国西部第一所', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
