INSERT INTO `school` VALUES ('1701', '辽宁传媒学院', '辽宁省教育厅', '0', '0', '21', '1991', '1500', '辽宁美术职业学院', '普通本科', '艺术类', '普通本科', '民办', '', '{}', '辽宁', '沈阳市', '', 'webmaster@fzcollege.org', '辽宁省沈阳市沈北新区沈北路30号', '110136', 'http://www.lncu.cn/zhaosheng/', '024-89745555', '', '辽宁传媒学院是经国家教育部批准建立的，东北地区唯一一所独立设置的传媒类本科大学，全国八大传媒院校之一。学校位于辽宁省省会沈阳市，总占地面积1500亩,建筑面积47万平方米，分南北两个校区，全日制在校生万人。学校是辽宁省应用型大学建设试点高校，辽宁省文化产业校企联盟理事长单位，荣获黄炎培“优秀学校奖”；就业率一直保持在96%以上，多次被评为辽宁普', '0', '0', '0', '0', 0, '0', '0', '0', '0', '96.32', '1.90', '0.00', '805', '662', '45.13', '54.87', '2', '2');
