INSERT INTO `school` VALUES ('1638', '吕梁学院', '山西省', '0', '0', '14', '1978', '1666', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '山西', '吕梁市', '', 'lxyzsjy@llu.edu.cn', '山西省吕梁市离石区学院路1号,汾阳师范分校地址：山西省吕梁市汾阳市文峰路8号', '033000', 'https://zsjyc.llu.edu.cn/zssy/sy.htm', '0358-8249991,0358-8249992', '', '吕梁学院坐落在山西省吕梁市新城区。吕梁市位于山西省西北部，是著名的革命老区，革命战争时期曾是红军东征主战场、晋绥边区首府和中央后委机关所在地，为中国革命胜利作出了重大贡献。一部《吕梁英雄传》，就是吕梁人民不畏牺牲、前赴后继的真实写照。近年来，全市人民主动适应经济发展新常态，谱写了脱贫攻坚、高质量发展的新篇章。2017年6月，习近平总书记到', '0', '0', '0', '0', 563, '464', '593', '0', '0', '78.37', '21.11', '0.00', '11583', '7173', '38.24', '61.76', '2', '2');
