INSERT INTO `school` VALUES ('1521', '琼台师范学院', '海南省', '46万', '0', '46', '1705', '1067', '琼台师范高等专科学校', '普通本科', '师范类', '普通本科', '公办', '', '{}', '海南', '海口市', '美兰区', 'qtsfzsb@163.com', '府城校区：海南省海口市琼山区府城中山路8号,桂林洋校区：海南省海口市美兰区桂林洋高校区', '571100', 'http://zhaosheng.qtnu.edu.cn/', '0898-66671919,0898-65897455', '0898-65731008', '琼台师范学院是海南省人民政府举办的省属公办全日制普通本科院校，坐落于我国最大的自由贸易港和唯一的热带岛屿——海南省的省会海口市。学校前身是创办于1705年的琼台书院。书院时期，琼台书院作为琼州唯一官办学府，被誉为科举时代琼州学子治学修身、登科入仕的不二之地。建国后，琼台作为海南幼儿与小学教师培养培训的主要基地，共向社会输送了8万余名各类专', '0', '0', '0', '0', 549, '499', '0', '0', '0', '96.68', '0.25', '0.16', '2060', '479', '18.87', '81.13', '2', '2');
