INSERT INTO `school` VALUES ('1256', '北部湾大学', '广西壮族自治区', '80万', '0', '45', '1973', '2070', '钦州学院', '普通本科', '综合类', '普通本科', '公办', '', '{}', '广西', '钦州市', '钦南区', 'qzxyzsb@126.com', '广西壮族自治区钦州市滨海大道12号', '535011', 'https://zsw.bbgu.edu.cn/sy.htm', '0777-2804088,0777-2804188', '0777-2807563', '北部湾大学是一所以工学、理学、管理学为主，多学科协调发展的综合性全日制普通高等学校，学校位于西部陆海新通道重要枢纽城市、北部湾经济区滨海城市——钦州市。具有岭南风格、滨海风光、东南亚风情特色的3A级景区校园，为师生员工提供了良好的教学、科研、学习和生活环境。一、历史沿革学校前身为1973年创办的钦州地区师范学校；1977年10月国务院宣布恢复高考', '2', '0', '0', '0', 437, '427', '649', '0', '0', '94.02', '1.82', '0.53', '1232', '1024', '45.39', '54.61', '2', '2');
