INSERT INTO `school` VALUES ('1253', '陆军军事交通学院', '解放军总后勤部', '0', '0', '12', '1973', '5000', '中国人民解放军军事交通学院,中国人民解放军陆军军事交通学院,中国人民解放军陆军军事交通学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '天津', '天津市', '河东区', 'rczp89@sohu.com', '天津市河东区向阳楼东局子1号', '300161', '', '022-84656318', '', '军事交通学院隶属于解放军总后勤部，是全军培养军事交通运输、军用车辆装备和军用物资储运领域高层次人才的最高学府，是集保障指挥与工程技术于一体、多科性教学科研型军事院校，主要面向全军培养本科、硕士、博士生，同时承担任职教育和外军留学生培训任务。在长期的办学过程中，学院秉承“忠诚、博学、自强、先行”的校训，弘扬艰苦奋斗、自强不息、求实创', '2', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
