INSERT INTO `school` VALUES ('1241', '宁夏师范学院', '宁夏回族自治区', '50万', '0', '64', '1975', '1393', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '宁夏', '固原市', '', 'nsyzb@163.com', '宁夏回族自治区固原市原州区新区学院路', '756000', 'https://zs.nxnu.edu.cn/', '0954-2079366,0954-2079368', '0954-2079602', '宁夏师范学院坐落于宁夏南部六盘山下历史名城——固原市，隶属宁夏回族自治区人民政府管理，是宁夏唯一的师范类本科院校。学校前身为1975年建立的六盘山大学，全国政协原副主席、时任固原地委副书记胡启立任六盘山大学筹备小组组长、党的核心领导小组组长。1978年经国务院批准在六盘山大学基础上建立固原师范专科学校，1994年更名为固原师范高等专科学校，2006年经', '4', '0', '0', '0', 420, '340', '0', '0', '0', '95.45', '5.23', '0.19', '1023', '448', '30.46', '69.54', '2', '2');
INSERT INTO `school` VALUES ('1239', '常州工业职业技术学院', '江苏省', '71万', '0', '32', '1958', '1000', '常州轻工职业技术学院', '专科（高职）', '理工类', '专科（高职）', '公办', '', '{}', '江苏', '常州市', '天宁区', 'hjh@czili.edu.cn', '江苏省常州市武进区鸣新中路28号', '213164', 'http://zs.ciit.edu.cn/', '400-990-2878,0519-86335008,0519-86335009', '0519-86335118', '常州工业职业技术学院，前身为常州轻工职业技术学院，是江苏省教育厅主管的公办全日制普通高等学校，是江苏省中国特色高水平高职学校建设单位、江苏省示范性高职院校。学校地处长江之南、太湖之滨——常州，毗邻人文荟萃、历史悠久的春秋淹城，坐落在集教学、科研、培训、职业技能鉴定和社会服务于一体的常州高等职业教育园区内。学校始建于1958年，已有60余年', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1238', '济宁医学院', '山东省', '150万', '0', '37', '1952', '1725', '', '普通本科', '医药类', '普通本科', '公办', '', '{}', '山东', '济宁市', '', 'zsb0537@163.com', '山东省济宁市太白湖新区荷花路133号', '272067', 'http://zhaosheng.jnmc.edu.cn/', '0537-3616888', '0537-3616019', '济宁医学院创建于1952年，是山东省属普通本科高等医科院校。学校现有济宁太白湖校区、任城校区和日照校区，占地总面积115万平方米，建筑面积67万平方米，与济宁国家高新区共建教学科技园。学校固定资产总值17.3亿元，其中教学科研仪器设备总值2.5亿元，馆藏图书总量为360余万册。学校建有2所直属附属医院，16所非隶属附属医院，170余个实践教学基地。济宁医学院附属医', '0', '0', '0', '0', 0, '357', '301', '0', '0', '96.41', '21.46', '0.05', '2900', '1349', '31.75', '68.25', '2', '2');
INSERT INTO `school` VALUES ('1236', '贵州工程应用技术学院', '贵州省', '0', '5', '52', '1938', '1223', '', '普通本科', '理工类', '普通本科', '公办', '', '{}', '贵州', '毕节市', '', 'bjxyjyzd@163.com', '贵州省毕节市七星关区学院路1号', '551700', 'http://www.gues.edu.cn/zsjy/zsfw.htm', '0857-8331317', '0857-8330068', '贵州工程应用技术学院创建于1938年，历经贵州省立毕节师范学校、毕节半耕半读师范学校、毕节师范专科学校等时期。1993年，教育部定名为毕节师范高等专科学校。2005年3月，毕节师范高等专科学校与毕节教育学院合并组建毕节学院，成为全日制普通本科高等学校。2014年5月，经教育部批准，毕节学院更名为贵州工程应用技术学院。学校系教育部“对口支援西部地区高等学校', '0', '0', '0', '0', 551, '418', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1234', '香港大学', '香港特别行政区', '0', '0', '81', '1911', '797', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '香港岛', '东区', 'prcua@hku.hk', '香港特别行政区香港岛中西区薄扶林道', '', '', '18721713647', '', '历史悠久——香港大学(港大)于1911年建校，是香港历史最悠久的大学。教研成绩显赫，校内有十名老师先后获选为中国科学院院士。港大长久以来肩负着香港、中国、亚洲及世界各地学术交流枢纽的重任，努力不懈为世界的学术交流和人才培养作出贡献。国际大学——香港大学为国际知名的高等学府，多次获本港及国际媒体高度评价为亚洲及太平洋地区(包括澳大利亚)最佳大学', '55', '35', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1233', '香港科技大学', '香港特别行政区', '0', '0', '81', '1991', '900', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '新界', '西贡区', 'msp@ust.edu.hk', '香港九龙半岛清水湾香港科技大学学术大楼G012室', '', 'https://join.usthk.cn/cn/', '0852-23588384', '', '香港科技大学（简称港科大）自1991年创立以来，秉持锐意创新、与时并进、博大包容、坚持原创的理念，在教育、科研和知识转移等方面均取得长足进展。短短三十年间，港科大于全球高校排名节节上升，被公认为全球首屈一指的年轻学府。身处瞬息万变的数字化时代，深深根植于香港的港科大，致力于推动全城的可持续发展。作为全球最开放、最具活力的经济体之一，与内', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1232', '重庆人文科技学院', '重庆市教委', '174万', '0', '50', '2003', '1575', '西南大学育才学院', '普通本科', '综合类', '普通本科', '民办', '', '{}', '重庆', '重庆市', '合川区', 'cqrk13548@cqrk.edu.cn', '重庆市合川区草街街道学院街256号', '401572', 'http://zsw.cqrk.edu.cn/', '023-42465352,023-42464905', '023-42461119', '重庆人文科技学院源于2000年创办的西南师范大学行知育才学院，2013年经教育部批准转设为由重庆市教委主管的普通本科高校。学校是全国应用技术型大学战略试点研究高校，重庆市硕士专业学位研究生教育培育试点单位，重庆市“三全育人”综合改革试点高校以及教育部思政司“一站式”学生社区综合管理模式建设自主试点高校。学校现有全日制在校生近23000人，现有14个二', '0', '0', '0', '0', 0, '0', '0', '0', '0', '90.26', '0.00', '0.00', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1231', '火箭军工程大学', '解放军火箭军', '0', '0', '61', '1951', '1600', '解放军第二炮兵工程大学', '普通本科', '军事类', '普通本科', '公办', '', '{A-: 1, B-: 1, C+: 2}', '陕西', '西安市', '灞桥区', 'zsepgc@sina.com', '陕西省西安市灞桥区同心路2号', '710025', '', '029-84744019', '', '第二炮兵工程大学地处历史文化名城、十三朝古都西安，距市中心约20公里，是我国唯一一所培养战略导弹部队指挥技术人才的高等军事院校，是全国全军重点院校，是军队“2110工程”重点建设院校。学校下设3个学院（理学院、初级指挥学院、士官职业技术教育学院）、7个专业系、2个研究生管理大队和若干学员旅。我校原是一所为地面炮兵培养初级指挥军官的军事院校。1951', '8', '4', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1230', '空军工程大学', '解放军空军', '0', '0', '61', '1999', '10300', '解放军空军工程大学,中国人民解放军空军工程大学,中国人民解放军空军工程大学', '普通本科', '军事类', '普通本科', '公办', '', '{B: 2, B-: 1}', '陕西', '西安市', '灞桥区', '', '陕西省西安市长乐东路甲字1号', '710051', 'http://www.afeu.cn:1000/', '029-84786226', '', '千年古都西安，在孕育半坡文明的浐河之滨、现代气息浓厚的浐灞生态园，坐落着一所庄严的军营、美丽的学府——空军工程大学。这里是人民空军高素质新型军事人才的摇篮，是军事科技创新的重要基地，更是青年学子献身国防、实现人生理想的知识殿堂。学校为全国重点大学，是全军重点建设的五所综合大学之一，是空军专业技术最高学府。下辖航空工程学院、防空反导', '17', '9', '0', '0', 0, '83', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1229', '陆军装甲兵学院', '解放军总装备部', '0', '0', '11', '1953', '0', '中国人民解放军装甲兵工程学院,中国人民解放军陆军装甲兵学院,中国人民解放军陆军装甲兵学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '北京', '北京市', '丰台区', 'zgyzsb@sina.com', '北京市丰台区杜家坎21号装甲兵工程学院', '100072', '', '010-66717531', '', '装甲兵工程学院座落在北京市风景秀丽的卢沟桥畔，是全国重点工科院校和全军十二所综合院校之一。学院的前身是\"哈尔滨军事工程学院\"装甲兵工程系，1961年在西安扩建为装甲兵工程学院，1969年迁址首都北京，是我军培养装甲机械化部队工程技术军官和指挥军官的最高学府。学院占地面积3700余亩，建有现代化的图书馆和电子阅览室，与国际互联网、北京图书馆和总部科技情', '6', '5', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1227', '陆军军医大学', '解放军总后勤部', '52万', '0', '50', '1954', '9079', '解放军第三军医大学,中国人民解放军陆军军医大学,中国人民解放军陆军军医大学', '普通本科', '军事类', '普通本科', '公办', '', '{}', '重庆', '重庆市', '沙坪坝区', 'admin@mail.tmmu.com.cn', '重庆市沙坪坝区高滩岩正街30号', '400038', '', '023-68753586', '', '中国人民解放军陆军军医大学是一所具有悠久历史和光荣传统的全国重点大学、军队重点建设院校、全国首批博士学位授权单位、首批开办八年制医学教育高校。大学前身可追溯到建于1937年的国立中正医学院、1941年的第二野战军医科大学和1946年的第四野战军医科学校。1954年原第六军医大学（南昌）、第七军医大学（重庆）合并组建第七军医大学，1975年更名为第三军医大学。', '12', '10', '0', '0', 0, '107', '0', '0', '1233', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1226', '陆军勤务学院', '解放军总后勤部', '0', '0', '50', '2017', '3000', '解放军后勤工程学院,中国人民解放军陆军勤务学院,中国人民解放军陆军勤务学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '重庆', '重庆市', '沙坪坝区', '', '重庆市沙坪坝区陈家桥大学城北一路20号', '400041', '', '023-68756243', '', '中国人民解放军后勤工程学院是军队高等工程技术院校。创办于1961年，1979年被确定为全国重点高等学校，是全军重点建设院校之一。1999年在全军率先荣获“教学优秀单位”称号。2006年，学院成为军队”2110工程“整体条件重点建设院校。学院创建于1961年，座落在美丽的山城重庆市区。占地面积1300多亩，建筑面积40多万平方米，图书馆藏书70余万册，建有校园网和电子阅览室。', '8', '5', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1224', '中国人民解放军西安通信学院', '解放军总参谋部', '0', '0', '61', '1945', '618', ',解放军西安通信学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '陕西', '西安市', '长安区', '', '陕西省西安市长安中路97号', '', '', '', '', '中国人民解放军西安通信学院，是一所为全军培养初级通信指挥军官和工程技术军官的通信专业院校，隶属于总参谋部。学院前身是东北民主联军总部通讯学校，始建于1945年。1986年改名为西安通信学院。学院定址于西安市长安中路97号，营区分为三部分，占地618.2亩。生活区分别位于长安中路和丈八东路，教学区位于西安市王曲镇。学院开设通信指挥、计算机科学与技术、信', '4', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1223', '解放军国际关系学院', '解放军总参谋部', '0', '0', '32', '1951', '615', '', '普通本科', '军事类', '普通本科', '公办', '', '{}', '江苏', '南京市', '雨花台区', '', '江苏省南京市雨花台区板桥街道国际关系学院训练部', '', '', '', '', '军事外交官的雄辩、睿智和智勇双全的形象，曾让多少人为之倾倒，也成为许多人心中的梦想。坐落在长江之滨的中国人民解放军国际关系学院，便是“中国军事外交官的摇篮”。从该校走出去的相当一部分毕业生，成为我军军事外交战线上的专家、教授和专门人才。解放军国际关系学院是全国重点高等院校之一，隶属于总参谋部，是一所指挥与技术合一，以外语为基础，多', '12', '3', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1222', '中国人民解放军战略支援部队信息工程大学', '解放军战略支援部队', '320万', '0', '41', '1949', '8000', '中国人民解放军信息工程大学,中国人民解放军战略支援部队信息工程大学', '普通本科', '军事类', '普通本科', '公办', '', '{A+: 2, A-: 3, B-: 1, C+: 1}', '河南', '郑州市', '', '2076703166@qq.com', '河南省郑州市中原区科学大道62号', '450001', '', '0371-81630228', '', '九曲黄河，浩浩汤汤，奔腾着中华民族复兴的梦想。中原大地，苍苍莽莽，负载着华夏儿女不灭的希望。黄河之滨，中原腹地，一座现代化高等军事学府在新世纪的曙光中崛起。这，就是中国人民解放军信息工程大学！中国人民解放军信息工程大学，由原解放军信息工程学院，测绘学院，电子技术学院合并组建而成，直属总参谋部领导，是全国重点理工科高等军事院校，全军', '17', '8', '0', '0', 0, '73', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1221', '香港城市大学', '香港特别行政区', '0', '0', '81', '1984', '234', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '九龙半岛', '深水埗区', 'mainland@cityu.edu.hk', '香港特别行政区九龙塘达之路83号', '', 'http://www.cityu.edu.hk/cityu/index-sc.htm', '0852-27889368', '（852）3442-7654', '一所致力提供专业教育及从事应用研究的大学香港城市大学(简称“城大”)是香港特别行政区政府资助的八所大专院校之一，是一所年轻向上、富于进取的国际化多科性大学。大学恪守「敬业乐群」之校训，致力提供专业教育及从事应用研究，每年为约25,000名学生提供超过180项不同层次及专业的优质本科及研究学位课程。建校至今，城大已建立了其富有特色的校园文化，发展为', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1220', '香港中文大学', '香港特别行政区', '0', '0', '81', '1963', '2060', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '香港', '新界', '沙田区', 'ugmain@cuhk.edu.hk', '香港特别行政区沙田区大学道附近', '', '', '00852-26098946', '', '香港中文大学（深圳）是一所经国家教育部批准，按中外合作办学条例设立的大学。大学以创建一所立足中国、面向世界的一流研究型大学为己任，致力于培养具有国际视野、中华传统和社会担当的创新型高层次人才。大学的办学特色是国际化的氛围、中英并重的教学环境、书院制传统、通识教育、新型交叉学科设置和以学生为本的育人理念。目前，来自世界各地的8000多名优', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1219', '山东大学（威海）', '教育部', '110万', '0', '37', '1984', '1600', '山东大学威海分校', '普通本科', '综合类', '普通本科', '公办', '双一流', '{}', '山东', '威海市', '环翠区', 'whzhaob@sdu.edu.cn', '山东省威海市环翠区文化西路180号', '264209', 'https://bkzsw.wh.sdu.edu.cn/', '0631-5688282,0631-5688902,0631-5688895', '', '山东大学是一所历史悠久、学科齐全、学术实力雄厚、办学特色鲜明，在国内外具有重要影响的教育部直属重点综合性大学，是世界一流大学建设高校（A类）。威海校区创建于1984年，是山东大学与威海市政府联合共建的多学科、开放式、综合性大学校区。她的诞生，开创了我国名校异地办学的先河；她的成长，印证了改革开放以来我国高等教育不断深化改革与科学发展的光辉', '24', '18', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '1', '1');
INSERT INTO `school` VALUES ('1218', '东北大学秦皇岛分校', '教育部', '101万', '0', '13', '1987', '701', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{}', '河北', '秦皇岛市', '海港区', 'zsb@mail.neuq.edu.cn', '河北省秦皇岛经济技术开发区泰山路143号', '066004', 'http://zs.neuq.edu.cn', '0335-8051798', '', '东北大学始建于１９２３年，是一所具有爱国主义光荣传统，学科结构完善，学术实力雄厚，在技术创新、转移和产学研合作等方面办学特色鲜明的教育部直属的国家重点大学，是国家首批“２１１工程”和“９８５工程”重点建设的高水平大学。２０１７年９月，经国务院批准，进入一流大学建设行列。在９０余年的办学历程中，东北大学始终坚持与国家发展和民族复兴同', '33', '11', '2', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '1', '1');
INSERT INTO `school` VALUES ('1217', '北京大学医学部', '教育部', '0', '0', '11', '1912', '0', '', '普通本科', '医药类', '普通本科', '公办', '双一流', '{}', '北京', '北京市', '海淀区', '', '北京市海淀区学院路38号北京大学医学部', '100191', 'http://jiaoyuchu.bjmu.edu.cn/zsjy/zsgz/index.htm', '010-82802223,82802191', '', '北京大学医学部（简称“北医”）地处北京市海淀区中关村科技园区海淀园，是中国政府创办的第一所西医院校，是国家“211工程”首批建设的高等学校之一，具有百年历史，集教学、科研、医疗为一体，学科覆盖医学门类中的基础医学、临床医学、口腔医学、药学、公共卫生和预防医学、护理学、医学技术、中西医结合以及理学、工学、教育学、哲学、文学等门类的部分学', '14', '12', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '1', '1');
INSERT INTO `school` VALUES ('1216', '宁波诺丁汉大学', '浙江省教育厅', '0', '0', '33', '2004', '887', '', '普通本科', '综合类', '普通本科', '中外合作办学', '', '{}', '浙江', '宁波市', '', 'Admissions@nottingham.edu.cn', '浙江省宁波市泰康东路199号', '315100', 'https://www.nottingham.edu.cn/cn/Study-with-us/home.aspx', '0574-88180182', '', '宁波诺丁汉大学是中国第一家经教育部批准引进世界百强优质高教资源的中外合作大学，颁发与英国诺丁汉大学相同的文凭。宁波诺丁汉大学地处宁波市高教园区，由宁波市人民政府主办，浙江万里学院与英国诺丁汉大学联办。宁波诺丁汉大学是中国第一家经教育部批准引进世界一流大学并具有独立法人资格、独立校园的中外合作大学。宁波诺丁汉大学的创办，使中国学生能', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1215', '上海师范大学天华学院', '上海市教委', '0', '0', '31', '2005', '501', '', '普通本科', '综合类', '普通本科', '民办', '', '{}', '上海', '上海市', '嘉定区', 'crisgong@hotmail.com', '上海市嘉定区胜辛北路1661号', '201815', 'http://zsxx.sthu.edu.cn/', '021-39966101,021-39966020', '021-39966666', '上海师范大学天华学院（ShanghaiNormalUniversityTIANHUACollege）于2005年4月经国家教育部批准建校，是一所全日制民办本科院校，独立招生，独立颁发毕业文凭，符合要求的学生按国家有关规定颁发学士学位。学校位于上海、江苏和浙江三省交界之处——上海市嘉定区，与上海市区有地铁11号线相通，随着城际高速铁路的建成，学校与长三角及全国的大中型城市的交通十分便捷。学校', '0', '0', '0', '0', 0, '0', '0', '0', '0', '98.01', '0.00', '0.00', '1133', '679', '37.50', '62.50', '2', '2');
INSERT INTO `school` VALUES ('1214', '集美大学诚毅学院', '福建省教育厅', '100万', '0', '35', '2003', '550', '', '普通本科', '综合类', '普通本科', '民办', '', '{}', '福建', '厦门市', '', 'cyzsb@jmu.edu.cn', '福建省厦门市集美区集美大道199号', '361021', 'http://cyzsb.jmu.edu.cn/', '0592-6182895,0592-6183328', '0592-6183185', '集美大学诚毅学院创办于2003年，是经国家教育部批准、由福建省重点建设高校集美大学与福建集美大学教育发展基金会联合举办的独立学院。学院地处著名华侨领袖陈嘉庚先生的故乡——福建厦门集美。学院坚持社会主义办学方向，弘扬嘉庚精神，秉承“诚毅”校训，培养基础扎实、知识面宽、特长明显，具有创新精神、实践能力和社会责任感的应用型人才，服务经济社会发', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1213', '武警工程大学', '武警总部', '0', '0', '61', '2000', '0', '武警工程学院，中国人民武装警察部队工程大学', '普通本科', '军事类', '普通本科', '公办', '', '{}', '陕西', '西安市', '新城区', '', '陕西省西安市沣东新城三桥武警路1号', '710086', '', '029-84562222', '', '中国人民武装警察部队工程大学，原名中国人民武装警察部队工程学院。这里是武警部队培养“讲政治、懂技术、会管理、能指挥，具有创新精神和实践能力，忠于党和人民的现代警官”的基地，进行科学研究的中心。她占地560亩，建筑面积20万平方米，建筑宏伟，绿树成荫，设施先进，环境幽雅，是读书治学的理想园地。学院原名中国人民武装警察部队技术学院，1983年经国', '6', '2', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1212', '辽宁警察学院', '辽宁省', '60万', '0', '21', '1960', '885', '辽宁警官高等专科学校', '普通本科', '政法类', '普通本科', '公办', '', '{}', '辽宁', '大连市', '', '', '辽宁省大连市甘井子区营平路260号', '116036', 'http://zsjy.lnpc.cn/', '0411-86729777,0411-86729865', '0411-86729999', '辽宁警察学院是全省唯一一所省属全日制公安本科院校，肩负着为全省公安司法机关培养应用型警务人才、培训在职民警的双重历史重任。2014年5月经教育部批准正式建立，前身为辽宁警官高等专科学校，学校创建于1960年，1985年建立大专，1995年开始独立试办本科专业。建校以来，已经为全省公安、司法等系统培养了4万多名优秀警务专门人才。学院以公安司法机关的改革需要', '0', '0', '0', '0', 0, '509', '0', '0', '0', '96.63', '0.00', '0.00', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('1211', '武警后勤学院', '武警后勤部', '12万', '0', '12', '1984', '1418', '', '普通本科', '军事类', '普通本科', '公办', '', '{}', '天津', '天津市', '东丽区', '', '天津市河东区成林道', '300162', '', '022-60578218', '', '中国人民武装警察部队后勤学院是在中国人民武装警察部队医学院的主体基础上，武警工程学院后勤专业并入而成，是武警部队惟一的高等医学院校，编制为副军级，隶属武警部队后勤部。学院位于天津市河东区成林道，占地面积703.8亩，总建筑面积21万余平方米。建院二十多年来，学院在武警部队党委和后勤部党委的正确领导下，艰苦奋斗，开拓进取，全面建设实现了跨越式', '1', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
