INSERT INTO `school` VALUES ('1160', '空军预警学院', '解放军空军', '0', '0', '42', '1952', '0', '空军预警学院,中国人民解放军空军预警学院', '普通本科', '军事类', '普通本科', '公办', '', '{}', '湖北', '武汉市', '江岸区', 'ldxyzsb@vip.163.com', '湖北省武汉市汉口黄埔大街288号', '430019', '', '027-85965235,85965225,85990220', '', '空军预警学院组建于1952年，原名空军雷达学院，2011年改建为空军预警学院。学院是全国唯一一所预警监视领域专门院校，全军唯一一所预警监视和电子对抗为主体学科专业的院校，是军队信息能力培训基地之一、国家一类本科军级军事院校，主要承担全军预警系统和空军地面电子对抗部队指挥技术军官及士官培养任务。学院采取“一院四地”办学，院本部位于湖北省武汉市，', '3', '3', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
