INSERT INTO `school` VALUES ('1110', '北京工商大学嘉华学院', '北京市教委', '55万', '0', '11', '2004', '540', '', '普通本科', '财经类', '普通本科', '民办', '', '{}', '北京', '北京市', '通州区', 'abc@abc.com', '北京市通州区宋庄南路甲1号', '101118', 'https://zsb.canvard.net.cn/', '010-69597736,010-69597746', '', '北京工商大学嘉华学院是由教育部2004年批准设立的全日制普通本科高等学校，国家计划内招生，颁发国家承认的毕业证书和学士学位证书。学校确立以国际化和高端应用型为发展战略目标，积极融入行业、融入国际，借助首都优质的教育资源，搭建应用型人才和国际化人才培养平台，致力于卓越的教学与服务，努力达成国内一流、国际知名的精英商学院的目标。学校地处北京', '0', '0', '0', '0', 0, '0', '0', '0', '0', '99.44', '1.85', '21.96', '560', '519', '48.10', '51.90', '2', '2');
