INSERT INTO `school` VALUES ('1090', '榆林学院', '陕西省', '0', '0', '61', '1958', '3540', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '陕西', '榆林市', '', 'jyzx916@163.com', '陕西省榆林市榆阳区崇文路51号', '719000', 'https://zsxxw.yulinu.edu.cn/', '0912-3896517', '0912-3893644', '榆林学院是一所以工科为主，工、管、文、理、农、法等学科协调发展的省属本科院校，是榆林市唯一省属本科院校。学校从绥德师范走来，办学历史可追溯至1923年。绥德师范是播种西北革命火种的地方，为中国革命和建设培养了2万余名杰出人才，3位校友成为副国级领导，60多位校友成为新中国党政军高级干部，8位校友担任过中共陕西省委书记，70多位校友为中国革命英勇献', '0', '0', '0', '0', 467, '452', '545', '0', '0', '91.91', '10.12', '0.00', '1544', '1707', '52.50', '47.50', '2', '2');
