INSERT INTO `school` VALUES ('1061', '厦门理工学院', '福建省', '0', '0', '35', '1981', '1927', '', '普通本科', '理工类', '普通本科', '公办', '', '{}', '福建', '厦门市', '', 'zsb@xmut.edu.cn', '福建省厦门市集美区理工路600号', '361024', 'https://zsb.xmut.edu.cn/', '0592-6291678', '', '厦门理工学院位于“高素质、高颜值、现代化、国际化”的海上花园城市——厦门，是福建省属公立本科大学，实行省市共建、以市为主的管理体制。学校秉持“以学生为本，为产业服务”的办学理念，遵循“明理精工，与时偕行”的校训，积极探索产学融合、校企合作、对外合作三位一体的服务地方发展道路，建设“亲产业、开放式、国际化”的国内一流高水平应用技术大', '4', '0', '0', '0', 277, '323', '412', '0', '0', '98.58', '4.74', '3.85', '2314', '2730', '54.12', '45.88', '2', '2');
