INSERT INTO `school` VALUES ('953', '江西航空职业技术学院', '江西省教育厅', '0', '0', '36', '1956', '2000', '', '专科（高职）', '理工类', '专科（高职）', '民办', '', '{}', '江西', '南昌市', '南昌县', 'aa88448942@163.com', '东校区：江西省南昌市青云谱区新溪桥东一路219号,西校区：江西省南昌市青云谱区新溪桥南一路41号', '330024', '', '0791-88448942', '0791-88401193', '江西航空职业技术学院创建于1956年，学校是中国人民解放军定向培养士官院校、国家高技能人才培养基地、教育部“全国应用型人才培养基地”，航空工业专业技术人才培训基地、世界技能大赛飞机维修项目国家集训基地。学校地处有着“物华天宝，人杰地灵”美誉的英雄城南昌市，现有洪都（东、西）、昌南两个校区。洪都校区位于我国第一架飞机、第一枚海防导弹诞生地', '0', '0', '0', '0', 0, '0', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
