INSERT INTO `school` VALUES ('867', '南昌工程学院', '江西省', '131万', '1', '36', '1958', '2047', '江西水利电力学院', '普通本科', '理工类', '普通本科', '公办', '', '{}', '江西', '南昌市', '青山湖区', 'zjb@nit.edu.cn', '瑶湖校区：江西省南昌市高新技术开发区天祥大道289号,彭家桥校区：江西省南昌市青山湖区北京东路59号', '330099', 'http://envo.nit.edu.cn/', '0791-88126666,0791-88126772', '', '南昌工程学院是一所以工学为主，管理学、理学、农学、文学、经济学、艺术学等多学科协调发展，水利电力特色鲜明的本科高校。学校原为水利部直属高校，2008年实现江西省人民政府与水利部共建。前身为1958年创建的江西水利电力学院，1978年恢复办学，历经江西省水利水电学校大专部、江西工学院水利分院、江西水利专科学校、南昌水利水电专科学校、南昌水利水电高等', '3', '0', '0', '0', 298, '357', '444', '0', '0', '', '', '', '', '', '', '', '2', '2');
