INSERT INTO `school` VALUES ('624', '唐山师范学院', '河北省', '170万', '0', '13', '1956', '1190', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '河北', '唐山市', '', 'zb@tstc.edu.cn', '校本部大学道校区：河北省唐山市建设北路156号,校本部学院路校区：河北省唐山市学院路41号', '063000', 'http://zsjy.tstc.edu.cn', '0315-3863129,0315-3863537', '', '唐山师范学院是河北省省属公立全日制普通本科高等院校。学校位于河北省唐山市高新技术开发区。唐山北依燕山，南临渤海，西与京津接壤，东与秦皇岛毗邻，是河北省经济强市。唐山区位优势突出、环境优美、底蕴深厚，为学校发展创造了优越条件。学校前身为创办于1956年的唐山速成师范专科学校，1988年被原国家教委评为“全国优秀师专”，2000年经教育部批准改建为本', '0', '0', '0', '0', 373, '418', '600', '0', '0', '89.23', '18.61', '0.41', '', '', '', '', '2', '2');
